package operatorss;

public class logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean num1=true,num2=false;
		/* logical AND */
    System.out.println("num1&&num2=" + (num1 && num2));
    //Logical OR
    System.out.println("num1||num2=" + (num1 || num2));
    //logical NOT//
    System.out.println("!(num1 !num2)=" + !(num1 && num2));
	}

}
