package operatorss;

public class relationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num1=12,num2=4;
//equal to//
System.out.println("num1==num2"+(num1==num2));
//not equal to//
System.out.println("num1!=num2"+(num1!=num2));
//greater than//
System.out.println("num1>num2"+(num1>num2));
//less than//
System.out.println("num1<num2"+(num1<num2));
//less than or equal to//
System.out.println("num1<= num2"+(num1<=num2));
//greater than or equal to//
System.out.println("num1>=num2"+(num1>=num2));
	}

}
