package operatorss;

public class unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int r=5;
System.out.println("r=:"+ r++);
System.out.println("r=:"+ r);
int p=7;
System.out.println("p=:"+ p--);
System.out.println("p=:"+ p);
int q=3;
System.out.println("q=:"+ ++q);
int s=4;
System.out.println("s=:"+ --s);
	}

}
